sysauto_script.script:
// get current time
var rightNow = new GlideDateTime();

// grab NI records that will be due in the next 24 hours and are not closed complete
var almostDueTasks = new GlideRecord('x_58872_needit_needit_task');
almostDueTasks.addQuery('due_date','>=',rightNow);
almostDueTasks.addQuery('due_date','<',gs.daysAgo(-1));
almostDueTasks.addQuery('state','!=',3);

// run query
almostDueTasks.query();

// write to log when almostDueTasks are found
// create event in event log
while(almostDueTasks.next()){
	gs.info("Almost due NeedIt Task record = " + almostDueTasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTask',almostDueTasks,almostDueTasks.number,gs.getUserName());
}
